import React from 'react'

function Cards({itens, search}) {
  return (
    <>
        {itens.filter((item)=>{
          if(search === "")
            return item
          else if((item.titulo || '').toLowerCase().includes((search || '').toLowerCase()))
            return item
        })
        .map((item) => (
            <div className="card">
                    
                <div className="cardHead">
                <p className="cardTitulo">{item.titulo}</p>
                
                <div className="etiquetas">
                    <div className="categoria">{item.categoria}</div>
                    <div className="dificuldade">{item.dificuldade}</div>
                </div>
                
                </div>

                <p className="cardDescricao">{item.descricao}
                </p>

            </div>
        ))}
    </>
  )
}

export default Cards
